{"changed":true,"filter":false,"title":"pins_controller.rb","tooltip":"/app/controllers/pins_controller.rb","value":"class PinsController < ApplicationController\n  before_action :set_pin, only: [:show, :edit, :update, :destroy]\n  before_action :correct_user, only: [:edit, :update, :destory]\n  before_action :authenticate_user!, except: [:index, :show]\n\n  respond_to :html\n\n  def index\n    @pins = Pin.all\n    respond_with(@pins)\n  end\n\n  def show\n    respond_with(@pin)\n  end\n\n  def new\n    @pin = current_user.pins.build\n    respond_with(@pin)\n  end\n\n  def edit\n  end\n\n  def create\n    @pin = current_user.pins.build(pin_params)\n    @pin.save\n    respond_with(@pin)\n  end\n\n  def update\n    @pin.update(pin_params)\n    respond_with(@pin)\n  end\n\n  def destroy\n    @pin.destroy\n    respond_with(@pin)\n  end\n\n  private\n    def set_pin\n      @pin = Pin.find(params[:id])\n    end\n    \n    def correct_user\n      @pin = current_user.pins.find_by(id: params[:id])\n      redirect_to pins_path, notice: \"Not authorized to edit this player\" if @pin.nil?\n    end\n\n    def pin_params\n      params.require(:pin).permit(:description, :string)\n    end\nend\n\n","undoManager":{"mark":99,"position":100,"stack":[[{"group":"doc","deltas":[{"start":{"row":2,"column":34},"end":{"row":2,"column":35},"action":"insert","lines":["y"]}]}],[{"group":"doc","deltas":[{"start":{"row":2,"column":35},"end":{"row":2,"column":36},"action":"insert","lines":[":"]}]}],[{"group":"doc","deltas":[{"start":{"row":2,"column":36},"end":{"row":2,"column":37},"action":"insert","lines":[" "]}]}],[{"group":"doc","deltas":[{"start":{"row":2,"column":37},"end":{"row":2,"column":39},"action":"insert","lines":["[]"]}]}],[{"group":"doc","deltas":[{"start":{"row":2,"column":38},"end":{"row":2,"column":39},"action":"insert","lines":[":"]}]}],[{"group":"doc","deltas":[{"start":{"row":2,"column":39},"end":{"row":2,"column":40},"action":"insert","lines":["e"]}]}],[{"group":"doc","delta